	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doSelectJoinAllExceptParts(Criteria $c, $con = null, $join_behavior = Criteria::LEFT_JOIN)
	{
		$c = clone $c;

		// Set the correct dbName if it has not been overridden
		// $c->getDbName() will return the same object if not set to another value
		// so == check is okay and faster
		if ($c->getDbName() == Propel::getDefaultDB()) {
			$c->setDbName(self::DATABASE_NAME);
		}

		PhotosPeer::addSelectColumns($c);
		$startcol2 = (PhotosPeer::NUM_COLUMNS - PhotosPeer::NUM_LAZY_LOAD_COLUMNS);

		MetaPeer::addSelectColumns($c);
		$startcol3 = $startcol2 + (MetaPeer::NUM_COLUMNS - MetaPeer::NUM_LAZY_LOAD_COLUMNS);

		AuthorsPeer::addSelectColumns($c);
		$startcol4 = $startcol3 + (AuthorsPeer::NUM_COLUMNS - AuthorsPeer::NUM_LAZY_LOAD_COLUMNS);

				$c->addJoin(array(PhotosPeer::META_ID,), array(MetaPeer::ID,), $join_behavior);
				$c->addJoin(array(PhotosPeer::AUTHOR_ID,), array(AuthorsPeer::ID,), $join_behavior);

		$stmt = BasePeer::doSelect($c, $con);
		$results = array();

		while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
			$key1 = PhotosPeer::getPrimaryKeyHashFromRow($row, 0);
			if (null !== ($obj1 = PhotosPeer::getInstanceFromPool($key1))) {
				// We no longer rehydrate the object, since this can cause data loss.
				// See http://propel.phpdb.org/trac/ticket/509
				// $obj1->hydrate($row, 0, true); // rehydrate
			} else {
				$omClass = PhotosPeer::getOMClass();

				$cls = substr('.'.$omClass, strrpos('.'.$omClass, '.') + 1);
				$obj1 = new $cls();
				$obj1->hydrate($row);
				PhotosPeer::addInstanceToPool($obj1, $key1);
			} // if obj1 already loaded

				// Add objects for joined Meta rows

				$key2 = MetaPeer::getPrimaryKeyHashFromRow($row, $startcol2);
				if ($key2 !== null) {
					$obj2 = MetaPeer::getInstanceFromPool($key2);
					if (!$obj2) {
	
						$omClass = MetaPeer::getOMClass();


					$cls = substr('.'.$omClass, strrpos('.'.$omClass, '.') + 1);
					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol2);
					MetaPeer::addInstanceToPool($obj2, $key2);
				} // if $obj2 already loaded

				// Add the $obj1 (Photos) to the collection in $obj2 (Meta)
				$obj2->addPhotos($obj1);

			} // if joined row is not null

				// Add objects for joined Authors rows

				$key3 = AuthorsPeer::getPrimaryKeyHashFromRow($row, $startcol3);
				if ($key3 !== null) {
					$obj3 = AuthorsPeer::getInstanceFromPool($key3);
					if (!$obj3) {
	
						$omClass = AuthorsPeer::getOMClass();


					$cls = substr('.'.$omClass, strrpos('.'.$omClass, '.') + 1);
					$obj3 = new $cls();
					$obj3->hydrate($row, $startcol3);
					AuthorsPeer::addInstanceToPool($obj3, $key3);
				} // if $obj3 already loaded

				// Add the $obj1 (Photos) to the collection in $obj3 (Authors)
				$obj3->addPhotos($obj1);

			} // if joined row is not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
	}


